import React, { useState } from 'react';
import axios from 'axios';

const EmailForm = () => {
  const [destinatario, setDestinatario] = useState('');
  const [asunto, setAsunto] = useState('');
  const [cuerpo, setCuerpo] = useState('');
  const [imagen, setImagen] = useState(null);

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    // Enviar el formulario
    try {
      const response = await axios.post('/api/send-email', {
        destinatario,
        asunto,
        cuerpo
      });

      console.log('Correo electrónico enviado:', response.data.message);
      alert('El correo electrónico ha sido enviado correctamente');
    } catch (error) {
      console.error('Error al enviar el correo electrónico:', error.response.data.error);
      alert('Ocurrió un error al enviar el correo electrónico');
    }
  };

  const handleImageUpload = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('imagen', e.target.files[0]);

    // Cargar la imagen
    try {
      const response = await axios.post('/api/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      console.log('Imagen cargada:', response.data.imageUrl);
      setImagen(response.data.imageUrl);
      alert('La imagen ha sido cargada correctamente');
    } catch (error) {
      console.error('Error al cargar la imagen:', error.response.data.error);
      alert('Ocurrió un error al cargar la imagen');
    }
  };

  return (
    <div>
      <h2>Enviar correo electrónico</h2>
      <form onSubmit={handleFormSubmit}>
        <div>
          <label htmlFor="destinatario">Destinatario:</label>
          <input
            type="email"
            id="destinatario"
            value={destinatario}
            onChange={(e) => setDestinatario(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="asunto">Asunto:</label>
          <input
            type="text"
            id="asunto"
            value={asunto}
            onChange={(e) => setAsunto(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="cuerpo">Cuerpo:</label>
          <textarea
            id="cuerpo"
            value={cuerpo}
            onChange={(e) => setCuerpo(e.target.value)}
            required
          ></textarea>
        </div>
        <div>
          <label htmlFor="imagen">Adjuntar imagen:</label>
          <input type="file" id="imagen" onChange={handleImageUpload} />
        </div>
        {imagen && <img src={imagen} alt="Imagen adjunta" />}
        <button type="submit">Enviar correo</button>
      </form>
    </div>
  );
};

export default EmailForm;
