import React, { useState } from 'react';
import axios from 'axios';
import Dropzone from 'react-dropzone';

const FileUploadForm = () => {
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileDrop = (acceptedFiles) => {
    setSelectedFile(acceptedFiles[0]);
  };

  const handleFileUpload = async () => {
    try {
      const formData = new FormData();
      formData.append('file', selectedFile);

      await axios.post('/api/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      alert('Archivo cargado correctamente');
    } catch (error) {
      console.error('Error al cargar el archivo:', error.response.data.error);
      alert('Ocurrió un error al cargar el archivo');
    }
  };

  return (
    <div>
      <h2>Carga de archivos</h2>
      <Dropzone onDrop={handleFileDrop}>
        {({ getRootProps, getInputProps }) => (
          <div {...getRootProps()}>
            <input {...getInputProps()} />
            <p>Arrastra y suelta un archivo aquí, o haz clic para seleccionar uno</p>
          </div>
        )}
      </Dropzone>
      {selectedFile && (
        <div>
          <p>Archivo seleccionado: {selectedFile.name}</p>
          <button onClick={handleFileUpload}>Cargar archivo</button>
        </div>
      )}
    </div>
  );
};

export default FileUploadForm;
